                        {items.slice(3, 6).map(item =>
                            <Card>
                                <CardImg top width="80%" src={item.image_url} alt={"No image"} />
                                <CardBody>
                                <CardTitle><Highlighter
                                    highlightClassName="HighlightClass"
                                    searchWords={this.state.input.split(" ")}
                                    autoEscape={true}
                                    textToHighlight={" " + item.first_name + " " + item.last_name}/></CardTitle>
                                <CardSubtitle>Title:<Highlighter
                                    highlightClassName="HighlightClass"
                                    searchWords={this.state.input.split(" ")}
                                    autoEscape={true}
                                    textToHighlight={" " + item.title}/></CardSubtitle>
                                <CardSubtitle>Party:<Highlighter
                                    highlightClassName="HighlightClass"
                                    searchWords={this.state.input.split(" ")}
                                    autoEscape={true}
                                    textToHighlight={" " + titleMap[item.party]}/></CardSubtitle>
                                <CardSubtitle>State:<Highlighter
                                    highlightClassName="HighlightClass"
                                    searchWords={this.state.input.split(" ")}
                                    autoEscape={true}
                                    textToHighlight={" " + item.state}/></CardSubtitle>
                                <CardSubtitle>Active from: Congress {item.congresses[0]["con_num"]} - Congress {item.congresses[item.congresses.length - 1]["con_num"]} </CardSubtitle>
                                    <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>
                    <CardDeck>
                        {items.slice(6, 10).map(item =>
                            <Card>
                                <CardImg top width="80%" src={item.image_url} /*onError={(e)=>{e.target.onerror = null; e.target.src={person}}}*/ alt={"No image"} />
                                <CardBody>
                                <CardTitle><Highlighter
                                    highlightClassName="HighlightClass"
                                    searchWords={this.state.input.split(" ")}
                                    autoEscape={true}
                                    textToHighlight={" " + item.first_name + " " + item.last_name}/></CardTitle>
                                <CardSubtitle>Title:<Highlighter
                                    highlightClassName="HighlightClass"
                                    searchWords={this.state.input.split(" ")}
                                    autoEscape={true}
                                    textToHighlight={" " + item.title}/></CardSubtitle>
                                <CardSubtitle>Party:<Highlighter
                                    highlightClassName="HighlightClass"
                                    searchWords={this.state.input.split(" ")}
                                    autoEscape={true}
                                    textToHighlight={" " + titleMap[item.party]}/></CardSubtitle>
                                <CardSubtitle>State:<Highlighter
                                    highlightClassName="HighlightClass"
                                    searchWords={this.state.input.split(" ")}
                                    autoEscape={true}
                                    textToHighlight={" " + item.state}/></CardSubtitle>
                                <CardSubtitle>Active from: Congress {item.congresses[0]["con_num"]} - Congress {item.congresses[item.congresses.length - 1]["con_num"]} </CardSubtitle>
                                <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>

                </Col>

                </div>
            <div>
				<MyPagination 
					pages = {this.state.pages} 
					handlePageClick = {this.handlePageClick}
					nextpage = {this.state.nextpage} 
					lastpage = {this.state.lastpage}
					page = {this.state.page}/> 
            </div>
            </div>
            </BrowserRouter>
        </div>

      );
    }
}

export default Representatives;
