import React, {Component} from "react";
import './Navbar.css';
import Highlighter from "react-highlight-words";
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Button, ButtonGroup, ButtonToolbar,
    Dropdown, DropdownToggle, DropdownMenu, DropdownItem,
    Table,Col, Pagination, PaginationItem, PaginationLink, Form, Input, FormGroup 
     } from 'reactstrap';
export class EnergyBills extends Component {
    constructor(props) {
        super(props);
        this.state = {
            terms: [],
            items: [],
            input:"",
            sortBy: "asc",
            filter: "",
            committee: "",
            year: "",
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            isLoading: false,
            dropdownOpen: [false, false],
            dropDownSelections: {"committee": "None", "year":"None"}
        }
        this.handleTermChange = this.handleTermChange.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
        this.handleEnter = this.handleEnter.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        
    }
    componentDidMount() {
        //this.setState({isLoading: true});
        var filter = this.state.filter;
        var committee = this.state.committee;
        var year = this.state.year;
        var search = "";

        if(committee !== ""){
            if(filter === ""){
                filter += '{"name":"committees","op":"like","val":"%'+ committee +'%"}'
            }
            else{
                filter += ',{"name":"committees","op":"like","val":"%'+ committee +'%"}'
            }

        }

        if(year !== ""){
            if(filter === ""){
                filter += '{"name":"introduced_date","op":"like","val":"'+ year +'%"}'
            }
            else{
                filter += ',{"name":"introduced_date","op":"like","val":"'+ year +'%"}'
            }

        }

        var input = this.state.input.split(" ");
        console.log(input)
        if(input[0] != ""){
            console.log("length")
            if(filter === ""){ 
                search = '{"or":[';
            }
            else{
                search = ',{"or":[';
            }
            search += '{"name":"title","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"sponsor_id","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"committees","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"introduced_date","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"cosponsors_by_party_R","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"house_passage","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"senate_passage","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"cosponsors_by_party_D","op":"like","val":"%'+ input[0] +'%"}';
          for(var i = 1; i < input.length; i++){
              search += ',{"name":"title","op":"like","val":"%'+ input[i] +'%"}';
              search += ',{"name":"sponsor_id","op":"like","val":"%'+ input[i] +'%"}';
              search += ',{"name":"committees","op":"like","val":"%'+ input[i] +'%"}';
              search += ',{"name":"introduced_date","op":"like","val":"%'+ input[i] +'%"}';
              search += ',{"name":"cosponsors_by_party_R","op":"like","val":"%'+ input[i] +'%"}';
              search += ',{"name":"house_passage","op":"like","val":"%'+ input[i] +'%"}';
              search += ',{"name":"senate_passage","op":"like","val":"%'+ input[i] +'%"}';
              search += ',{"name":"cosponsors_by_party_D","op":"like","val":"%'+ input[i] +'%"}';
          }
      
          search += "]}";
          filter += search;
        }

         if(filter === ""){
            fetch('https://api.empoweringknowledge.me/api/bills?q={"order_by":[{"field":"title","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, pages:data.total_pages,isLoading: false});      
            })
            .catch(console.log)
        }
        else{
            fetch('https://api.empoweringknowledge.me/api/bills?q={"filters":[' + filter + '],"order_by":[{"field":"title","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, pages:data.total_pages,isLoading: false}, () => {
                    if(this.state.pages <= 1)
                        this.setState({nextpage: 1})
                });      
            })
            .catch(console.log)
        }
    }
    
  handleTermChange(e) {

      this.setState({ input: e.target.value });
    
  }
  getName(value){
    if(value != null){
        return ("" + value.first_name + " " + value.last_name)
    }
  }
  handleSearch() {
    
    this.componentDidMount();
  }

  handleEnter(e) {
      
      if (e.key === 13) {
      this.handleSearch();
      }
  }

  handleSubmit(e) {
      e.preventDefault();
      this.handleSearch();
}
    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
        
    }

    handleFilterCommittee(committeeFilter){
        console.log(committeeFilter);
        this.setState({committee: committeeFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleFilterYear(yearFilter){
        console.log(yearFilter);
        this.setState({year: yearFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleSorting(sort){
        this.setState({sortBy: sort}, () => {
        this.componentDidMount();
        });
    }

    handleReset(){
        this.setState({dropDownSelections: {"committee":"None", "year": "None"}, input: "", filter: "", committee: "", year: "", page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }


    setDropdownOpen(index){
        var newdropdownOpen = this.state.dropdownOpen;
        newdropdownOpen[index] = !newdropdownOpen[index];
        this.setState({dropdownOpen: newdropdownOpen});
    }

    setDropDownTitle(attribute, selection){
        var newDropDownSelections = this.state.dropDownSelections;
        newDropDownSelections[attribute] = selection;
        this.setState({dropDownSelections: newDropDownSelections});
        if(selection === "None"){
            selection = "";
        }
        if(attribute === "committee")
            this.handleFilterCommittee(selection);
        else if(attribute === "year")
            this.handleFilterYear(selection);
    }

    renderSortAndFilter(){
      return(
        <div>
           <div className="text-left">
                <ButtonGroup>
                    <Dropdown isOpen={this.state.dropdownOpen[0]} toggle={() => this.setDropdownOpen(0)}>
                        <DropdownToggle caret>
                            Filter by Committee: {this.state.dropDownSelections["committee"]}
                        </DropdownToggle>
                        <DropdownMenu
                            modifiers={{
                              setMaxHeight: {
                                enabled: true,
                                order: 890,
                                fn: (data) => {
                                  return {
                                    ...data,
                                    styles: {
                                      ...data.styles,
                                      overflow: 'auto',
                                      maxHeight: 150,
                                    },
                                  };
                                },
                              },
                            }}
                        >
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "None")}>None</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Agriculture")}>Agriculture</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Appropriations")}>Appropriations</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Armed Services")}>Armed Services</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Budget")}>Budget</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Banking")}>Banking</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Education")}>Education</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Energy")}>Energy</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Ethics")}>Ethics</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Financial Services")}>Financial Services</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Foreign")}>Foreign</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Homeland Security")}>Homeland Security</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Administration")}>Administration</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Judiciary")}>Judiciary</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Natural Resources")}>Natural Resources</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Oversight and Reform")}>Oversight and Reform</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Rules")}>Rules</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Administration")}>Administration</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Science")}>Science</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Small Business")}>Small Business</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Transportation")}>Transportation</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Veterans' Affairs")}>Veterans' Affairs</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Ways and Means")}>Ways and Means</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Judiciary")}>Judiciary</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("committee", "Natural Resources")}>Natural Resources</DropdownItem>
                        </DropdownMenu>{' '}
                    </Dropdown>
                    <Dropdown isOpen={this.state.dropdownOpen[1]} toggle={() => this.setDropdownOpen(1)}>
                        <DropdownToggle caret>
                            Filter by Year: {this.state.dropDownSelections["year"]}
                        </DropdownToggle>
                        <DropdownMenu
                          modifiers={{
                              setMaxHeight: {
                                enabled: true,
                                order: 890,
                                fn: (data) => {
                                  return {
                                    ...data,
                                    styles: {
                                      ...data.styles,
                                      overflow: 'auto',
                                      maxHeight: 150,
                                    },
                                  };
                                },
                              },
                            }}
                        >
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "None")}>None</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "1993")}>1993</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "1994")}>1994</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "1995")}>1995</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "1996")}>1996</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "1997")}>1997</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "1998")}>1998</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "1999")}>1999</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2000")}>2000</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2001")}>2001</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2002")}>2002</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2003")}>2003</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2004")}>2004</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2005")}>2005</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2006")}>2006</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2007")}>2007</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2008")}>2008</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2009")}>2009</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2010")}>2010</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2011")}>2011</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2012")}>2012</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2013")}>2013</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2014")}>2014</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2015")}>2015</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2017")}>2017</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2018")}>2018</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("year", "2019")}>2019</DropdownItem>
                        </DropdownMenu>{' '}
                    </Dropdown>
                    <Button onClick={() => this.handleReset()}>Reset</Button>{' '}
                </ButtonGroup>
            </div>
            <div className="text-right">
                <Button color="primary" onClick={() => this.handleSorting("asc")}>Ascending</Button>{' '}
                <Button color="secondary"onClick={() => this.handleSorting("desc")}>Descending</Button>{' '}
            </div>   
        </div>
      );
    }  


    render() {
        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)   
        
      return (
        <div>
            <div>
                {this.renderSortAndFilter()}
            </div>
        <div className="EnergyBills" style={{paddingTop: "2%",width:"100%", height:"60%"}}>
          <Form className="searchbox" onSubmit={this.handleSubmit}>
                <FormGroup row>
              
                <Col sm={{ size: 'auto', offset: 5 }}>
                    <Input
                    type="text"
                    placeholder="Search"
                    onChange={this.handleTermChange}
                    onKeyDown={this.handleEnter}
                    />
                </Col>
                </FormGroup>

                </Form>
           <div>
            <h4>Energy Bills</h4>
            <Col sm={{ size: 'auto'}} style={{paddingTop: "2%"}}>
            <Table hover responsive>
                <thead>
                <tr>
                    <th></th>
                    <th>Title</th>
                    <th>Sponsor</th>
                    <th>Committees</th>
                    <th>Introduced Date</th>
                    <th>House Passage</th>
                    <th>Senate Passage</th>
                    <th>Democrat Cosponsors</th>
                    <th>Republican Cosponsors</th>
                </tr>
                </thead>
                <tbody>
                    
                {items.map(item =>

                <tr>
                    <th scope="row"></th>
                    <td> <a href= {"/bill/" + item.bill_id}   to={"/bill/" + item.bill_id}><Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.title}/></a></td>
                    <td> <a href= {"/politician/" + item.sponsor_id}   to={"/politician/" + item.sponsor_id}><Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + this.getName(item.sponsor_rs)}/></a></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.committees}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.introduced_date}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.house_passage}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.senate_passage}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.cosponsors_by_party_D}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.cosponsors_by_party_R}/></td>
                </tr>
                )}
                </tbody>
            </Table>
                </Col>
            </div>
        <div>
        <Pagination size="lg" style={{justifyContent: 'center', paddingTop:'3%'}} aria-label="Page navigation example">
        <PaginationItem>
          <PaginationLink first onClick={() => this.handlePageClick(1)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink previous onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"} />
        </PaginationItem>
        {/* <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"}>
            {this.state.lastpage}
          </PaginationLink>
        </PaginationItem> */}
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.page)} href={"#"}>
            {this.state.page}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"}>
            {this.state.nextpage}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink next onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink last onClick={() => this.handlePageClick(this.state.pages)} href="#" />
        </PaginationItem>
      </Pagination>
      </div>
        </div>
    </div>
      );
    }
}

export default EnergyBills;
