import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Col,
    Card, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck, Pagination, PaginationItem, PaginationLink
    } from 'reactstrap';
import {BrowserRouter} from 'react-router-dom';
export class States extends Component {
    constructor(props) {
        console.log("constructor")
        super(props);
        this.state = {
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            items: [],
            isLoading: false,
            name: ""
        }
        
        
    }

    componentDidMount() {
        console.log(this.state.page)
        this.setState({isLoading: true});
        fetch('https://dm5v6ty0dwipl.cloudfront.net/api/states' + "?page=" + this.state.page)
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,pages:data.total_pages,isLoading: false});      
        })
        .catch(console.log)
    }

    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
        
    }
    
    render() {
        var stateMap = {
            "Alabama":"al",
            "Alaska":"ak",
            "Arizona":"az",
            "Arkansas":"ar",
            "California":"ca",
            "Colorado":"co",
            "Connecticut":"ct",
            "Delaware":"de",
            "Florida":"fl",
            "Georgia":"ga",
            "Hawaii":"hi",
            "Idaho":"id",
            "Illinois":"il",
            "Indiana":"in",
            "Iowa":"ia",
            "Kansas":"ks",
            "Kentucky":"ky",
            "Louisiana":"la",
            "Maine":"me",
            "Maryland":"md",
            "Massachusetts":"ma",
            "Michigan":"mi",
            "Minnesota":"mn",
            "Mississippi":"ms",
            "Missouri":"mo",
            "Montana":"mt",
            "Nebraska":"ne",
            "Nevada":"nv",
            "New Hampshire":"nh",
            "New Jersey":"nj",
            "New Mexico":"nm",
            "New York":"ny",
            "North Carolina":"nc",
            "North Dakota":"nd",
            "Ohio":"oh",
            "Oklahoma":"ok",
            "Oregon":"or",
            "Pennsylvania":"pa",
            "Rhode Island":"ri",
            "South Carolina":"sc",
            "South Dakota":"sd",
            "Tennessee":"tn",
            "Texas":"tx",
            "Utah":"ut",
            "Vermont":"vt",
            "Virginia":"va",
            "Washington":"wa",
            "West Virginia":"wv",
            "Wisconsin":"wi",
            "Wyoming":"wy",
          };
        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        <div className="States" style={{paddingTop: "100px",width:"100%", height:"60%"}}>
           <div>
           
           <Col sm={{ size: 'auto'}}>
                <CardDeck>
                    {items.slice(0, 3).map(item =>
                        <Card>
                            <img top width="80%" style={{width: '300px', height: '375px'}} src={"https://www.state-maps.org/"+ stateMap[item.name]+".gif"} alt={"No image"} />
                            <CardBody>
                            <CardTitle>{item.name}</CardTitle>
                            <CardSubtitle>Population: {item.population}</CardSubtitle>
                            <CardSubtitle>Median Income: {item.median_hh_income}</CardSubtitle>
                            <CardSubtitle>Past and Present Representatives: {item.party}</CardSubtitle>
                            <Button href= {"/state/" + item.name} class="nav-link"  to={"/state/" + item.name}> See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>
                <CardDeck>
                    {items.slice(3, 6).map(item =>
                        <Card>
                            <img top width="80%" style={{width: '300px', height: '375px'}} src={"https://www.state-maps.org/"+ stateMap[item.name]+".gif"} alt={"No image"} />
                            <CardBody>
                            <CardTitle>{item.name}</CardTitle>
                            <CardSubtitle>Population: {item.population}</CardSubtitle>
                            <CardSubtitle>Median Income: {item.median_hh_income}</CardSubtitle>
                            <CardSubtitle>Past and Present Representatives: {item.party}</CardSubtitle>
                                <Button href= {"/state/" + item.name} class="nav-link"  to={"/state/" + item.name} > See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>
                <CardDeck>
                    {items.slice(6, 10).map(item =>
                        <Card>
                            <img top width="80%" style={{width: '300px', height: '375px'}} src={"https://www.state-maps.org/"+ stateMap[item.name]+".gif"} alt={"No image"} />
                            <CardBody>
                            <CardTitle>{item.name}</CardTitle>
                            <CardSubtitle>Population: {item.population}</CardSubtitle>
                            <CardSubtitle>Median Income: {item.median_hh_income}</CardSubtitle>
                            <CardSubtitle>Past and Present Representatives: {item.party}</CardSubtitle>
                            <Button href= {"/state/" + item.name} class="nav-link"  to={"/state/" + item.name}> See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>

            
            </Col>
            
            </div>
            <div>
        <Pagination size="lg" style={{justifyContent: 'center', paddingTop:'3%'}} aria-label="Page navigation example">
        <PaginationItem>
          <PaginationLink first onClick={() => this.handlePageClick(1)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink previous onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"} />
        </PaginationItem>
        {/* <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"}>
            {this.state.lastpage}
          </PaginationLink>
        </PaginationItem> */}
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.page)} href={"#"}>
            {this.state.page}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"}>
            {this.state.nextpage}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink next onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink last onClick={() => this.handlePageClick(this.state.pages)} href="#" />
        </PaginationItem>
      </Pagination>
      </div>
        </div>
        
      );
    }
}

export default States;
