import React, {Component} from "react";
import {
    Button, ButtonGroup, ButtonToolbar,
    Col,
    Card, CardBody,
    CardTitle, CardSubtitle, CardDeck, 
    Dropdown, DropdownToggle, DropdownMenu, DropdownItem,
    Pagination, PaginationItem, PaginationLink,  Form, Input, FormGroup 
    } from 'reactstrap';
import {BrowserRouter} from 'react-router-dom';

import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export class States extends Component {
    constructor(props) {
        console.log("constructor")
        super(props);
        this.state = {
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            items: [],
            input: "",
            itemsToDisplay: [],
            isLoading: false,
            sortBy: "asc",
            filter: "",
            name: "",
            party: "",
            population: {"lower": "", "upper": ""},
            median: {"lower": "", "upper": ""},
            resource: "",
            dropdownOpen: [false, false, false, false],
            dropDownSelections: {"party":"None", "population": "None", "median":"None", "resource":"None"}
        } 
        this.handleTermChange = this.handleTermChange.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
        this.handleEnter = this.handleEnter.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this); 
    }

    componentDidMount() {
        console.log(this.state.page)
        this.setState({isLoading: true});
        var filter = this.state.filter;
        var population = this.state.population;
        var median = this.state.median;
        var resource = this.state.resource;
        var party = this.state.party;
        var search = "";

        if(party !== ""){
            if(filter === ""){
                filter += '{"name":"dominant_party","op":"eq","val":"'+ party +'"}'
            }
            else{
                filter += ',{"name":"dominant_party","op":"eq","val":"'+ party +'"}'
            }

        }
        if(population["lower"] !== ""){
            if(filter === ""){
                filter += '{"name":"population","op":"ge","val":"'+ population["lower"] +'"}'
            }
            else{
                filter += ',{"name":"population","op":"ge","val":"'+ population["lower"] +'"}'
            }

            if(population["upper"] !== "")
                filter += ',{"name":"population","op":"le","val":"'+ population["upper"] +'"}'
        }
        if(median["lower"] !== ""){
            if(filter === ""){
                filter += '{"name":"median_hh_income","op":"gt","val":"'+ median["lower"] +'"}'
            }
            else{
                filter += ',{"name":"median_hh_income","op":"ge","val":"'+ median["lower"] +'"}'
            }

            if(median["upper"] !== "")
                filter += ',{"name":"median_hh_income","op":"le","val":"'+ median["upper"] +'"}'

        }
        if(resource !== ""){
            if(filter === ""){
                filter += '{"name":"primary_energy_source_1","op":"eq","val":"'+ resource +'"}'
            }
            else{
                filter += ',{"name":"primary_energy_source_1","op":"eq","val":"'+ resource +'"}'
            }

        }

       
        var input = this.state.input.split(" ");
        console.log(input)
        if(input[0] != ""){
            console.log("length")
            if(filter === ""){ 
                search = '{"or":[';
            }
            else{
                search = ',{"or":[';
            }
            search += '{"name":"title","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"sponsor_id","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"committees","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"introduced_date","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"cosponsors_by_party_R","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"house_passage","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"senate_passage","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"cosponsors_by_party_D","op":"like","val":"%'+ input[0] +'%"}';
            

          for(var i = 1; i < input.length; i++){
            search += ',{"name":"title","op":"like","val":"%'+ input[i] +'%"}';
            search += ',{"name":"sponsor_id","op":"like","val":"%'+ input[i] +'%"}';
            search += ',{"name":"committees","op":"like","val":"%'+ input[i] +'%"}';
            search += ',{"name":"introduced_date","op":"like","val":"%'+ input[i] +'%"}';
            search += ',{"name":"cosponsors_by_party_R","op":"like","val":"%'+ input[i] +'%"}';
            search += ',{"name":"house_passage","op":"like","val":"%'+ input[i] +'%"}';
            search += ',{"name":"senate_passage","op":"like","val":"%'+ input[i] +'%"}';
            search += ',{"name":"cosponsors_by_party_D","op":"like","val":"%'+ input[i] +'%"}';
          }
      
          search += "]}";
          filter += search;
        }
        console.log(filter);
        if(filter === ""){
            console.log('https://api.empoweringknowledge.me/api/states?q={"order_by":[{"field":"name","direction":"'
            + this.state.sortBy 
            + '"}]}&page='
            + this.state.page);
            fetch('https://api.empoweringknowledge.me/api/states?q={"order_by":[{"field":"name","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, itemsToDisplay: data.objects, pages:data.total_pages,isLoading: false});      
            })
            .catch(console.log)
        }
        else{
            console.log('https://api.empoweringknowledge.me/api/states?q={"filters":[' + filter + '],"order_by":[{"field":"name","direction":"'
            + this.state.sortBy 
            + '"}]}&page='
            + this.state.page);
            fetch('https://api.empoweringknowledge.me/api/states?q={"filters":[' + filter + '],"order_by":[{"field":"name","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, itemsToDisplay: data.objects, pages:data.total_pages,isLoading: false}, () => {
                    if(this.state.pages <= 1)
                        this.setState({nextpage: 1})    
                });      
            })
            .catch(console.log)
        }
    }

    handleTermChange(e) {

        this.setState({ input: e.target.value });
      
    }
  
    handleSearch() {
      
      this.componentDidMount();
    }
  
    handleEnter(e) {
        
        if (e.key === 13) {
        this.handleSearch();
        }
    }
  
    handleSubmit(e) {
        e.preventDefault();
        this.handleSearch();
    }

    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
        
    }

    handleSorting(sort){
        this.setState({sortBy: sort}, () => {
        this.componentDidMount();
        });

    }

    handleFilterParty(partyFilter){
        console.log(partyFilter);
        this.setState({party: partyFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleFilterPopulation(populationFilter){
        this.setState({population: populationFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleFilterMedian(medianFilter){
        this.setState({median: medianFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleFilterResource(resourceFilter){
        this.setState({resource: resourceFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleReset(){
        this.setState({dropDownSelections: {"party":"None", "population": "None", "median":"None", "resource":"None"}, filter: "", party: "", population: {"lower": "", "upper": ""}, median: {"lower": "", "upper": ""}, resource: "", page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    setDropdownOpen(index){
        var newdropdownOpen = this.state.dropdownOpen;
        newdropdownOpen[index] = !newdropdownOpen[index];
        this.setState({dropdownOpen: newdropdownOpen});
    }

    setDropDownTitle(attribute, selection){
        var newDropDownSelections = this.state.dropDownSelections;
        newDropDownSelections[attribute] = selection;
        this.setState({dropDownSelections: newDropDownSelections});
        if(selection === "None"){
            selection = "";
        }
        if(attribute === "party")
            this.handleFilterParty(selection);
        else if(attribute === "population"){
            switch(selection){
                case "1 - 999,999":
                    selection = {"lower": "1", "upper": "999999"}
                    break;
                case "1,000,000 - 9,999,999":
                    selection = {"lower": "1000000", "upper": "9999999"}
                    break;
                case "10,000,000 - 19,999,999":
                    selection = {"lower": "10000000", "upper": "19999999"}
                    break;
                case "20,000,000":
                    selection = {"lower": "20000000", "upper": ""}
                    break;
                default:
                    selection = {"lower": "", "upper": ""}
            }
            this.handleFilterPopulation(selection);
        }
        else if(attribute === "median"){
            switch(selection){
                case "1 - 49,999":
                    selection = {"lower": "1", "upper": "49999"}
                    break;
                case "50,000 - 59,999":
                    selection = {"lower": "50000", "upper": "59999"}
                    break;
                case "60,000 - 69,999":
                    selection = {"lower": "60000", "upper": "69999"}
                    break;
                case "70,000":
                    selection = {"lower": "70000", "upper": ""}
                    break;
                default:
                    selection = {"lower": "", "upper": ""}
            }
            this.handleFilterMedian(selection);
        }
        else if(attribute === "resource")
            this.handleFilterResource(selection);
    }

    renderSortAndFilter(){
        return(
            <div>
                <div className="text-left">
                    <ButtonGroup>
                        <Dropdown isOpen={this.state.dropdownOpen[0]} toggle={() => this.setDropdownOpen(0)}>
                            <DropdownToggle caret>
                                Filter by Party: {this.state.dropDownSelections["party"]}
                            </DropdownToggle>
                            <DropdownMenu>
                                <DropdownItem onClick={() => this.setDropDownTitle("party", "None")}>None</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("party", "Democratic")}>Democratic</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("party", "Republican")}>Republican</DropdownItem>
                            </DropdownMenu>{' '}
                        </Dropdown>
                        <Dropdown isOpen={this.state.dropdownOpen[1]} toggle={() => this.setDropdownOpen(1)}>
                            <DropdownToggle caret>
                                Filter by Population: {this.state.dropDownSelections["population"]}
                            </DropdownToggle>
                            <DropdownMenu>
                                <DropdownItem onClick={() => this.setDropDownTitle("population", "None")}>None</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("population", "1 - 999,999")}>1 - 999,999</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("population", "1,000,000 - 9,999,999")}>1,000,000 - 9,999,999</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("population", "10,000,000 - 19,999,999")}>10,000,000 - 19,999,999</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("population", "20,000,000")}> > 20,000,000</DropdownItem>
                            </DropdownMenu>{' '}
                        </Dropdown>
                        <Dropdown isOpen={this.state.dropdownOpen[2]} toggle={() => this.setDropdownOpen(2)}>
                            <DropdownToggle caret>
                                Filter by Income: {this.state.dropDownSelections["median"]}
                            </DropdownToggle>
                            <DropdownMenu>
                                <DropdownItem onClick={() => this.setDropDownTitle("median", "None")}>None</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("median", "1 - 49,999")}>1 - 49,999</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("median", "50,000 - 59,999")}>50,000 - 59,999</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("median", "60,000 - 69,999")}>60,000 - 69,999</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("median", "70,000")}> >70,000</DropdownItem>
                            </DropdownMenu>{' '}
                        </Dropdown>
                        <Dropdown isOpen={this.state.dropdownOpen[3]} toggle={() => this.setDropdownOpen(3)}>
                            <DropdownToggle caret>
                                Filter by Energy: {this.state.dropDownSelections["resource"]}
                            </DropdownToggle>
                            <DropdownMenu>
                                <DropdownItem onClick={() => this.setDropDownTitle("resource", "None")}>None</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("resource", "NuclearPower")}>Nuclear Power</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("resource", "NaturalGas")}>Natural Gas</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("resource", "Coal")}>Coal</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("resource", "Petroleum")}>Petroleum</DropdownItem>
                                <DropdownItem onClick={() => this.setDropDownTitle("resource", "Hydroelectricity")}>Hydroelectricity</DropdownItem>
                            </DropdownMenu>{' '}
                        </Dropdown>
                        <Button onClick={() => this.handleReset()}>Reset</Button>{' '}
                    </ButtonGroup>
                </div>
                <div className="text-right">
                    <Button color="primary" onClick={() => this.handleSorting("asc")}>Ascending</Button>{' '}
                    <Button color="secondary"onClick={() => this.handleSorting("desc")}>Descending</Button>{' '}
                </div>
            </div>
        );
    }
    
    render() {
        var stateMap = {
            "Alabama":"al",
            "Alaska":"ak",
            "Arizona":"az",
            "Arkansas":"ar",
            "California":"ca",
            "Colorado":"co",
            "Connecticut":"ct",
            "Delaware":"de",
            "Florida":"fl",
            "Georgia":"ga",
            "Hawaii":"hi",
            "Idaho":"id",
            "Illinois":"il",
            "Indiana":"in",
            "Iowa":"ia",
            "Kansas":"ks",
            "Kentucky":"ky",
            "Louisiana":"la",
            "Maine":"me",
            "Maryland":"md",
            "Massachusetts":"ma",
            "Michigan":"mi",
            "Minnesota":"mn",
            "Mississippi":"ms",
            "Missouri":"mo",
            "Montana":"mt",
            "Nebraska":"ne",
            "Nevada":"nv",
            "New Hampshire":"nh",
            "New Jersey":"nj",
            "New Mexico":"nm",
            "New York":"ny",
            "North Carolina":"nc",
            "North Dakota":"nd",
            "Ohio":"oh",
            "Oklahoma":"ok",
            "Oregon":"or",
            "Pennsylvania":"pa",
            "Rhode Island":"ri",
            "South Carolina":"sc",
            "South Dakota":"sd",
            "Tennessee":"tn",
            "Texas":"tx",
            "Utah":"ut",
            "Vermont":"vt",
            "Virginia":"va",
            "Washington":"wa",
            "West Virginia":"wv",
            "Wisconsin":"wi",
            "Wyoming":"wy",
          };
        const {itemsToDisplay, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(itemsToDisplay)        
        
      return (
        <div>
            <div>
                {this.renderSortAndFilter()}
            </div>
            <Form className="searchbox" onSubmit={this.handleSubmit}>
                <FormGroup row>
              
                <Col sm={{ size: 'auto', offset: 5 }}>
                    <Input
                    type="text"
                    placeholder="Search"
                    onChange={this.handleTermChange}
                    onKeyDown={this.handleEnter}
                    />
                </Col>
                </FormGroup>

                </Form>
            <div className="States" style={{paddingTop: "100px",width:"100%", height:"60%"}}>
               <div>
               
               <Col sm={{ size: 'auto'}}>
                    <CardDeck>
                        {itemsToDisplay.slice(0, 3).map(item =>
                            <Card>
                                <img top width="80%" style={{width: '300px', height: '375px'}} src={"https://www.state-maps.org/"+ stateMap[item.name]+".gif"} alt={"No image"} />
                                <CardBody>
                                <CardTitle>{item.name}</CardTitle>
                                <CardSubtitle>Dominate Party: {item.dominant_party}</CardSubtitle>
                                <CardSubtitle>Population: {item.population}</CardSubtitle>
                                <CardSubtitle>Median Income: {item.median_hh_income}</CardSubtitle>
                                <CardSubtitle>Primary Energy Source: {item.primary_energy_source_1}</CardSubtitle>
                                <Button href= {"/state/" + item.name} class="nav-link"  to={"/state/" + item.name}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>
                    <CardDeck>
                        {itemsToDisplay.slice(3, 6).map(item =>
                            <Card>
                                <img top width="80%" style={{width: '300px', height: '375px'}} src={"https://www.state-maps.org/"+ stateMap[item.name]+".gif"} alt={"No image"} />
                                <CardBody>
                                <CardTitle>{item.name}</CardTitle>
                                <CardSubtitle>Dominate Party: {item.dominant_party}</CardSubtitle>
                                <CardSubtitle>Population: {item.population}</CardSubtitle>
                                <CardSubtitle>Median Income: {item.median_hh_income}</CardSubtitle>
                                <CardSubtitle>Primary Energy Source: {item.primary_energy_source_1}</CardSubtitle>
                                    <Button href= {"/state/" + item.name} class="nav-link"  to={"/state/" + item.name} > See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>
                    <CardDeck>
                        {itemsToDisplay.slice(6, 10).map(item =>
                            <Card>
                                <img top width="80%" style={{width: '300px', height: '375px'}} src={"https://www.state-maps.org/"+ stateMap[item.name]+".gif"} alt={"No image"} />
                                <CardBody>
                                <CardTitle>{item.name}</CardTitle>
                               <CardSubtitle>Dominate Party: {item.dominant_party}</CardSubtitle>
                                <CardSubtitle>Population: {item.population}</CardSubtitle>
                                <CardSubtitle>Median Income: {item.median_hh_income}</CardSubtitle>
                                <CardSubtitle>Primary Energy Source: {item.primary_energy_source_1}</CardSubtitle>
                                <Button href= {"/state/" + item.name} class="nav-link"  to={"/state/" + item.name}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>

                
                </Col>
                
                </div>
                <div>
            <Pagination size="lg" style={{justifyContent: 'center', paddingTop:'3%'}} aria-label="Page navigation example">
            <PaginationItem>
              <PaginationLink first onClick={() => this.handlePageClick(1)} href={"#"} />
            </PaginationItem>
            <PaginationItem>
              <PaginationLink previous onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"} />
            </PaginationItem>
            {/* <PaginationItem>
              <PaginationLink onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"}>
                {this.state.lastpage}
              </PaginationLink>
            </PaginationItem> */}
            <PaginationItem>
              <PaginationLink onClick={() => this.handlePageClick(this.state.page)} href={"#"}>
                {this.state.page}
              </PaginationLink>
            </PaginationItem>
            <PaginationItem>
              <PaginationLink onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"}>
                {this.state.nextpage}
              </PaginationLink>
            </PaginationItem>
            <PaginationItem>
              <PaginationLink next onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"} />
            </PaginationItem>
            <PaginationItem>
              <PaginationLink last onClick={() => this.handlePageClick(this.state.pages)} href="#" />
            </PaginationItem>
          </Pagination>
          </div>
            </div>
        </div>
      );
    }
}

export default States;
