import React, {Component} from "react";
import './App.css';
import Navigation from './Navbar.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Route, BrowserRouter} from 'react-router-dom';
import {Representatives} from './Representatives.js';
import {About} from './About.js';
import {Home} from './Home.js';
import {States} from './States.js';
import {EnergyBills} from './EnergyBills.js';
import {RepresentativeInstance} from './RepresentativeInstance.js';
import {EnergyBillsInstance} from './EnergyBillsInstance.js';
import {StatesInstance} from './StatesInstance.js';




// import { Navbar, Nav, NavItem, NavDropdown, MenuItem, Button, Form, FormControl} from 'react-bootstrap';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        marcykaptur: "marcykaptur",
        peterwelch: "peterwelch",
        johncornyn: "johncornyn",
        data: {0: ["Marcy Kaptur", "Ohio", "Democrat", "1983-present", "House"], 1: ["Peter Welch","Vermont", "Democrat", "2007-present", "House"], 2: ["John Cornyn","Texas", "Republican", "2002-present", "Senate"]} ,
        bills: {0: ["S. 1685: Launching Energy Advancement and Development through Innovations for Natural Gas Act of 2019", "John Cornyn", "2019", "N/A", "Republican", "Senate"], 1: ["H.R. 2960 Energy and Water Development and Related Agencies Appropriations Act","Marcy Kaptur","2019", "N/A", "1 Democrat", "House"], 2: ["H.R.2746 - American Renewable Energy and Efficiency Act","Peter Welch","2017", "N/A", "11 Democrat", "House"]},
        states: {0: ["Ohio","11,690,000", "656.19 billion", "Midwest", "Republican"], 1:["Vermont", "626,299", "19.3 trillion", "Northeast", "Democrat"], 2:["Texas", "28,700,000", "1.645 trillion", "Southwest", "Republican"]}
      };
    }
  render(){
    return (
      <div className="App">
        <Navigation/>
        <BrowserRouter>
          <Route exact path="/" component={Home}  />
          <Route path={"/politicians"} component={Representatives}  />
          <Route path={"/about"} component={About}  />
          <Route path={"/home"} component={Home}  />
          <Route path={"/bills"} component={EnergyBills}  />
          <Route path={"/states"} component={States}  />
          <Route path={"/state/"} render={(props) => <StatesInstance {...props} />} />
          <Route path={"/bill/"} render={(props) => <EnergyBillsInstance {...props} />} />
          <Route path={"/politician/"} render={(props) => <RepresentativeInstance {...props}  />} />
       </BrowserRouter>                        
      
        
      </div>
    );
  }
}
  

export default App;
