import React, {Component} from "react";
import {
    Button, ButtonGroup, ButtonToolbar,
    Col,
    Card, CardImg, CardBody, CardTitle, CardSubtitle, CardDeck,
    Dropdown, DropdownToggle, DropdownMenu, DropdownItem,
    Pagination, PaginationItem, PaginationLink
     } from 'reactstrap';
import { BrowserRouter} from 'react-router-dom';

import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {RepresentativeInstance} from './RepresentativeInstance.js';
import person from './emptyperson.png';

export class Representatives extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            input: [],
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            sortBy: "asc",
            filter: "",
            title: "",
            party: "",
            state: "", 
            district: "", 
            isLoading: false,
            dropdownOpen: [false, false, false, false],
            dropDownSelections: {"title":"None", "party": "None", "state":"None", "district":"None"}
        }
        // this.handleTermChange = this.handleTermChange.bind(this);
        // this.handleSearch = this.handleSearch.bind(this);
        // this.handleEnter = this.handleEnter.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this); 
    }
    componentDidMount() {
        console.log(this.state.page)
        this.setState({isLoading: true});
        var filter = this.state.filter;
        var title = this.state.title;
        var party = this.state.party;
        var state = this.state.state;
        var district = this.state.district;
        var search = "";

        if(title !== ""){
            if(filter === ""){
                filter += '{"name":"title","op":"like","val":"%'+ title +'%"}'
            }
            else{
                filter += ',{"name":"title","op":"like","val":"%'+ title +'%"}'
            }

        }
        if(party !== ""){
            if(filter === ""){
                filter += '{"name":"party","op":"eq","val":"'+ party +'"}'
            }
            else{
                filter += ',{"name":"party","op":"eq","val":"'+ party +'"}'
            }
        }
        if(state !== ""){
            if(filter === ""){
                filter += '{"name":"state","op":"eq","val":"'+ state +'"}'
            }
            else{
                filter += ',{"name":"state","op":"eq","val":"'+ state +'"}'
            }

        }

        var input = this.state.input.split(" ");
        console.log(input)
        if(input[0] != ""){
            console.log("length")
            if(filter === ""){ 
                search = '{"or":[';
            }
            else{
                search = ',{"or":[';
            }
            search += '{"name":"first_name","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"last_name","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"title","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"party","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"state","op":"like","val":"%'+ input[0] +'%"}';

          for(var i = 1; i < input.length; i++){
            search += ',{"name":"first_name","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"last_name","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"title","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"party","op":"like","val":"%'+ input[0] +'%"}';
            search += ',{"name":"state","op":"like","val":"%'+ input[0] +'%"}';
          }
      
          search += "]}";
          filter += search;
        }

        if(filter === ""){
            fetch('https://api.empoweringknowledge.me/api/congressmembers?q={"order_by":[{"field":"first_name","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, pages:data.total_pages,isLoading: false});      
            })
            .catch(console.log)
        }
        else{
            fetch('https://api.empoweringknowledge.me/api/congressmembers?q={"filters":[' + filter + '],"order_by":[{"field":"first_name","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, pages:data.total_pages,isLoading: false}, () => {
                    if(this.state.pages <= 1)
                        this.setState({nextpage: 1})
                });      
            })
            .catch(console.log)
        }
    }

    // refetch(){
    //     console.log(this.state.page)
    //     this.setState({isLoading: true});
    //     fetch('https://dm5v6ty0dwipl.cloudfront.net/api/congressmembers'+ "?page=" + this.state.page)
    //     .then(res => res.json())
    //     .then((data) => {
    //         this.setState({ items: data.objects,isLoading: false,pages:data.total_pages});      
    //     })
    //     .catch(console.log)
    // }

    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
    }

    handleSorting(sort){
        this.setState({sortBy: sort}, () => {
        this.componentDidMount();
        });

    }

    handleFilterTitle(titleFilter){
        this.setState({title: titleFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

     handleFilterParty(partyFilter){
        this.setState({party: partyFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleFilterState(stateFilter){
        this.setState({state: stateFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleReset(){
        this.setState({dropDownSelections: {"title":"None", "party": "None", "state":"None", "district":"None"}, filter: "", title: "", party: "", state: "", district: "", page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }


    setDropdownOpen(index){
        var newdropdownOpen = this.state.dropdownOpen;
        newdropdownOpen[index] = !newdropdownOpen[index];
        this.setState({dropdownOpen: newdropdownOpen});
    }

    setDropDownTitle(attribute, selection){
        var newDropDownSelections = this.state.dropDownSelections;
        newDropDownSelections[attribute] = selection;
        this.setState({dropDownSelections: newDropDownSelections});
        if(selection === "None"){
            selection = "";
        }
        if(attribute === "title")
            this.handleFilterTitle(selection);
        else if(attribute === "party"){
            switch(selection){
                case "Democratic":
                    selection = "D"
                    break;
                case "Republican":
                    selection = "R"
                    break;
            }
            this.handleFilterParty(selection);
        }
        else if(attribute === "state")
            this.handleFilterState(selection);
        else if(attribute === "district"){
            switch(selection){
                case "Congress 100 - Congress 105":
                    selection = {"lower": "100", "upper": "105"}
                    break;
                case "50,000 - 59,999":
                    selection = {"lower": "50000", "upper": "59999"}
                    break;
                case "60,000 - 69,999":
                    selection = {"lower": "60000", "upper": "69999"}
                    break;
                case "70,000":
                    selection = {"lower": "70000", "upper": ""}
                    break;
                default:
                    selection = {"lower": "", "upper": ""}
            }
            this.handleFilterMedian(selection);
        }
    }


    renderSortAndFilter(){
        return(
            <div>
                <div className="text-left">
                        <ButtonGroup>
                            <Dropdown isOpen={this.state.dropdownOpen[0]} toggle={() => this.setDropdownOpen(0)}>
                                <DropdownToggle caret>
                                    Filter by Title: {this.state.dropDownSelections["title"]}
                                </DropdownToggle>
                                <DropdownMenu>
                                    <DropdownItem onClick={() => this.setDropDownTitle("title", "None")}>None</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("title", "Representative")}>Representative</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("title", "Senator")}>Senator</DropdownItem>
                                </DropdownMenu>{' '}
                            </Dropdown>
                            <Dropdown isOpen={this.state.dropdownOpen[1]} toggle={() => this.setDropdownOpen(1)}>
                                <DropdownToggle caret>
                                    Filter by Party: {this.state.dropDownSelections["party"]}
                                </DropdownToggle>
                                <DropdownMenu>
                                    <DropdownItem onClick={() => this.setDropDownTitle("party", "None")}>None</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("party", "Democratic")}>Democratic</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("party", "Republican")}>Republican</DropdownItem>
                                </DropdownMenu>{' '}
                            </Dropdown>
                            <Dropdown isOpen={this.state.dropdownOpen[2]} toggle={() => this.setDropdownOpen(2)}>
                                <DropdownToggle caret>
                                    Filter by State: {this.state.dropDownSelections["state"]}
                                </DropdownToggle>
                                <DropdownMenu
                                    modifiers={{
                                      setMaxHeight: {
                                        enabled: true,
                                        order: 890,
                                        fn: (data) => {
                                          return {
                                            ...data,
                                            styles: {
                                              ...data.styles,
                                              overflow: 'auto',
                                              maxHeight: 150,
                                            },
                                          };
                                        },
                                      },
                                    }}
                                >
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "None")}>None</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Alabama")}>Alabama</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Alaska")}>Alaska</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Arizona")}>Arizona</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Arkansas")}>Arkansas</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "California")}>California</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Colorado")}>Colorado</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Connecticut")}>Connecticut</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Delaware")}>Delaware</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Florida")}>Florida</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Georgia")}>Georgia</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Hawaii")}>Hawaii</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Idaho")}>Idaho</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Illinois")}>Illinois</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Indiana")}>Indiana</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Iowa")}>Iowa</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Kansas")}>Kansas</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Kentucky")}>Kentucky</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Louisiana")}>Louisiana</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Maine")}>Maine</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "None")}>None</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "Maryland")}>Maryland</DropdownItem>
