import React, {Component} from "react";
import {
    Button, ButtonGroup, ButtonToolbar,
    Col,
    Card, CardImg, CardBody, CardTitle, CardSubtitle, CardDeck,
    Dropdown, DropdownToggle, DropdownMenu, DropdownItem,
    Pagination, PaginationItem, PaginationLink
     } from 'reactstrap';
import { BrowserRouter} from 'react-router-dom';

import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {RepresentativeInstance} from './RepresentativeInstance.js';
import person from './emptyperson.png';

export class Representatives extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            sortBy: "asc",
            filter: "",
            title: "",
            party: "",
            state: "", 
            active: "", 
            isLoading: false,
            dropdownOpen: [false, false, false, false],
            dropDownSelections: {"title":"None", "party": "None", "state":"None", "active":"None"}
        }
        
    }
    componentDidMount() {
        console.log(this.state.page)
        this.setState({isLoading: true});
        var filter = this.state.filter;
        var title = this.state.title;
        var party = this.state.party;
        var state = this.state.state;
        var active = this.state.active;
        var search = "";

        if(title !== ""){
            if(filter === ""){
                filter += '{"name":"title","op":"like","val":"%'+ title +'%"}'
            }
            else{
                filter += ',{"name":"title","op":"like","val":"%'+ title +'%"}'
            }

        }
        if(party !== ""){
            if(filter === ""){
                filter += '{"name":"party","op":"eq","val":"'+ party +'"}'
            }
            else{
                filter += ',{"name":"party","op":"ge","val":"'+ party +'"}'
            }
        }

        //     if(population["upper"] !== "")
        //         filter += ',{"name":"population","op":"le","val":"'+ population["upper"] +'"}'
        // }
        // if(median["lower"] !== ""){
        //     if(filter === ""){
        //         filter += '{"name":"median_hh_income","op":"gt","val":"'+ median["lower"] +'"}'
        //     }
        //     else{
        //         filter += ',{"name":"median_hh_income","op":"ge","val":"'+ median["lower"] +'"}'
        //     }

        //     if(median["upper"] !== "")
        //         filter += ',{"name":"median_hh_income","op":"le","val":"'+ median["upper"] +'"}'

        // }
        // if(resource !== ""){
        //     if(filter === ""){
        //         filter += '{"name":"primary_energy_source_1","op":"eq","val":"'+ resource +'"}'
        //     }
        //     else{
        //         filter += ',{"name":"primary_energy_source_1","op":"eq","val":"'+ resource +'"}'
        //     }

        // }

        if(filter === ""){
            fetch('https://api.empoweringknowledge.me/api/congressmembers?q={"order_by":[{"field":"first_name","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, pages:data.total_pages,isLoading: false});      
            })
            .catch(console.log)
        }
        else{
            fetch('https://api.empoweringknowledge.me/api/congressmembers?q={"filters":[' + filter + '],"order_by":[{"field":"first_name","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, pages:data.total_pages,isLoading: false}, () => {
                    if(this.state.pages <= 1)
                        this.setState({nextpage: 1})
                });      
            })
            .catch(console.log)
        }
    }

    // refetch(){
    //     console.log(this.state.page)
    //     this.setState({isLoading: true});
    //     fetch('https://dm5v6ty0dwipl.cloudfront.net/api/congressmembers'+ "?page=" + this.state.page)
    //     .then(res => res.json())
    //     .then((data) => {
    //         this.setState({ items: data.objects,isLoading: false,pages:data.total_pages});      
    //     })
    //     .catch(console.log)
    // }

    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
    }

    handleSorting(sort){
        this.setState({sortBy: sort}, () => {
        this.componentDidMount();
        });

    }

    handleFilterTitle(titleFilter){
        this.setState({title: titleFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

     handleFilterParty(partyFilter){
        this.setState({party: partyFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    setDropdownOpen(index){
        var newdropdownOpen = this.state.dropdownOpen;
        newdropdownOpen[index] = !newdropdownOpen[index];
        this.setState({dropdownOpen: newdropdownOpen});
    }

    setDropDownTitle(e, attribute, selection){
        console.log(e);
        var newDropDownSelections = this.state.dropDownSelections;
        newDropDownSelections[attribute] = selection;
        this.setState({dropDownSelections: newDropDownSelections});
        if(selection === "None"){
            selection = "";
        }
        if(attribute === "title")
            this.handleFilterTitle(selection);
        else if(attribute === "party"){
            switch(selection){
                case "Democratic":
                    selection = "D"
                    break;
                case "Republican":
                    selection = "R"
                    break;
            }
            this.handleFilterParty(selection);
        }
        else if(attribute === "median"){
            switch(selection){
                case "1 - 49,999":
                    selection = {"lower": "1", "upper": "49999"}
                    break;
                case "50,000 - 59,999":
                    selection = {"lower": "50000", "upper": "59999"}
                    break;
                case "60,000 - 69,999":
                    selection = {"lower": "60000", "upper": "69999"}
                    break;
                case "70,000":
                    selection = {"lower": "70000", "upper": ""}
                    break;
                default:
                    selection = {"lower": "", "upper": ""}
            }
            this.handleFilterMedian(selection);
        }
        else if(attribute === "resource")
            this.handleFilterResource(selection);
    }


    renderSortAndFilter(){
        return(
            <div>
                <div className="text-left">
                        <ButtonGroup>
                            <Dropdown isOpen={this.state.dropdownOpen[0]} toggle={() => this.setDropdownOpen(0)}>
                                <DropdownToggle caret>
                                    Filter by Title: {this.state.dropDownSelections["title"]}
                                </DropdownToggle>
                                <DropdownMenu>
                                    <DropdownItem onClick={() => this.setDropDownTitle("title", "None")}>None</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("title", "Representative")}>Representative</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("title", "Senator")}>Senator</DropdownItem>
                                </DropdownMenu>{' '}
                            </Dropdown>
                            <Dropdown isOpen={this.state.dropdownOpen[1]} toggle={() => this.setDropdownOpen(1)}>
                                <DropdownToggle caret>
                                    Filter by Party: {this.state.dropDownSelections["party"]}
                                </DropdownToggle>
                                <DropdownMenu>
                                    <DropdownItem onClick={() => this.setDropDownTitle(this,"party", "None")}>None</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("party", "Democratic")}>Democratic</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("party", "Republican")}>Republican</DropdownItem>
                                </DropdownMenu>{' '}
                            </Dropdown>
                            <Dropdown isOpen={this.state.dropdownOpen[2]} toggle={() => this.setDropdownOpen(2)}>
                                <DropdownToggle caret>
                                    Filter by State: {this.state.dropDownSelections["state"]}
                                </DropdownToggle>
                                <DropdownMenu>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "None")}>None</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "1 - 49,999")}>1 - 49,999</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "50,000 - 59,999")}>50,000 - 59,999</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "60,000 - 69,999")}>60,000 - 69,999</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("state", "70,000")}> >70,000</DropdownItem>
                                </DropdownMenu>{' '}
                            </Dropdown>
                            <Dropdown isOpen={this.state.dropdownOpen[3]} toggle={() => this.setDropdownOpen(3)}>
                                <DropdownToggle caret>
                                    Filter by Active: {this.state.dropDownSelections["active"]}
                                </DropdownToggle>
                                <DropdownMenu>
                                    <DropdownItem onClick={() => this.setDropDownTitle("active", "None")}>None</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("active", "NuclearPower")}>Nuclear Power</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("active", "NaturalGas")}>Natural Gas</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("active", "Coal")}>Coal</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("active", "Petroleum")}>Petroleum</DropdownItem>
                                    <DropdownItem onClick={() => this.setDropDownTitle("active", "Hydroelectricity")}>Hydroelectricity</DropdownItem>
                                </DropdownMenu>{' '}
                            </Dropdown>
                            <Button onClick={() => this.handleReset()}>Reset</Button>{' '}
                        </ButtonGroup>
                    </div>
                <div className="text-right">
                    <Button color="primary" onClick={() => this.handleSorting("asc")}>Ascending</Button>{' '}
                    <Button color="secondary"onClick={() => this.handleSorting("desc")}>Descending</Button>{' '}
                </div>
            </div>
        );
    }
    
    render() {
        var titleMap ={
            "D": "Democratic",
            "R": "Republican",
        };

        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        <div>
            <div>
                {this.renderSortAndFilter()}
            </div>
            <BrowserRouter>
            <div className="Representatives" style={{width:"100%", height:"60%"}}>
            
               <div>

                <Col sm={{ size: 'auto'}}>
                    <CardDeck>
                        {items.slice(0, 3).map(item =>
                            <Card>
                                <CardImg top width="80%" src={item.image_url}  alt={"No image"} />
                                <CardBody>
                                <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                                <CardSubtitle>Title:  {item.title}</CardSubtitle>
                                <CardSubtitle>Party: {titleMap[item.party]}</CardSubtitle>
                                <CardSubtitle>State: {item.state}</CardSubtitle>
                                <CardSubtitle>Active from: Congress {item.congresses[0]["con_num"]} - Congress {item.congresses[item.congresses.length - 1]["con_num"]}</CardSubtitle>
                                <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>
                    <CardDeck>
                        {items.slice(3, 6).map(item =>
                            <Card>
                                <CardImg top width="80%" src={item.image_url} alt={"No image"} />
                                <CardBody>
                                <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                                <CardSubtitle>Title:  {item.title}</CardSubtitle>
                                <CardSubtitle>Party: {titleMap[item.party]}</CardSubtitle>
                                <CardSubtitle>State: {item.state}</CardSubtitle>
                                <CardSubtitle>Active from: Congress {item.congresses[0]["con_num"]} - Congress {item.congresses[item.congresses.length - 1]["con_num"]} </CardSubtitle>
                                    <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>
                    <CardDeck>
                        {items.slice(6, 10).map(item =>
                            <Card>
                                <CardImg top width="80%" src={item.image_url} /*onError={(e)=>{e.target.onerror = null; e.target.src={person}}}*/ alt={"No image"} />
                                <CardBody>
                                <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                                <CardSubtitle>Title:  {item.title}</CardSubtitle>
                                <CardSubtitle>Party: {titleMap[item.party]}</CardSubtitle>
                                <CardSubtitle>State: {item.state}</CardSubtitle>
                                <CardSubtitle>Active from: Congress {item.congresses[0]["con_num"]} - Congress {item.congresses[item.congresses.length - 1]["con_num"]} </CardSubtitle>
                                <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>

                </Col>

                </div>
                <div>
            <Pagination size="lg" style={{justifyContent: 'center', paddingTop:'3%'}} aria-label="Page navigation example">
            <PaginationItem>
              <PaginationLink first onClick={() => this.handlePageClick(1)} href={"#"} />
            </PaginationItem>
            <PaginationItem>
              <PaginationLink previous onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"} />
            </PaginationItem>
            {/* <PaginationItem>
              <PaginationLink onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"}>
                {this.state.lastpage}
              </PaginationLink>
            </PaginationItem> */}
            <PaginationItem>
              <PaginationLink onClick={() => this.handlePageClick(this.state.page)} href={"#"}>
                {this.state.page}
              </PaginationLink>
            </PaginationItem>
            <PaginationItem>
              <PaginationLink onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"}>
                {this.state.nextpage}
              </PaginationLink>
            </PaginationItem>
            <PaginationItem>
              <PaginationLink next onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"} />
            </PaginationItem>
            <PaginationItem>
              <PaginationLink last onClick={() => this.handlePageClick(this.state.pages)} href="#" />
            </PaginationItem>
          </Pagination>
          </div>
            </div>
            </BrowserRouter>
        </div>

      );
    }
}

export default Representatives;
