import React, {Component} from "react";
import './Navbar.css';
import Highlighter from "react-highlight-words";
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Button, ButtonGroup, ButtonToolbar,
    Dropdown, DropdownToggle, DropdownMenu, DropdownItem,
    Table,Col, Pagination, PaginationItem, PaginationLink, Form, Input, FormGroup 
     } from 'reactstrap';
export class EnergyBills extends Component {
    constructor(props) {
        super(props);
        this.state = {
            terms: [],
            items: [],
            input:"",
            sortBy: "asc",
            filter: "",
            committee: "",
            year: "",
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            isLoading: false,
            dropdownOpen: [false, false],
            dropDownSelections: {"committee": "None", "year":"None"}
        }
        this.handleTermChange = this.handleTermChange.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
        this.handleEnter = this.handleEnter.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        
    }
    componentDidMount() {
        //this.setState({isLoading: true});
        var input = this.state.input.split(" ");
        var filter = this.state.filter;

        console.log(input)
        var url = 'https://api.empoweringknowledge.me/api/bills?page=' + this.state.page;
        if(input[0] != ""){
          url = 'https://api.empoweringknowledge.me/api/bills?q={"filters":[{"or":[';
          url += '{"name":"title","op":"like","val":"%'+ input[0] +'%"}';
          url += ',{"name":"sponsor_id","op":"like","val":"%'+ input[0] +'%"}';
          url += ',{"name":"committees","op":"like","val":"%'+ input[0] +'%"}';
          url += ',{"name":"introduced_date","op":"like","val":"%'+ input[0] +'%"}';
          url += ',{"name":"cosponsors_by_party_R","op":"like","val":"%'+ input[0] +'%"}';
          url += ',{"name":"house_passage","op":"like","val":"%'+ input[0] +'%"}';
          url += ',{"name":"senate_passage","op":"like","val":"%'+ input[0] +'%"}';
          url += ',{"name":"cosponsors_by_party_D","op":"like","val":"%'+ input[0] +'%"}';
          for(var i = 1; i < input.length; i++){
              url += ',{"name":"title","op":"like","val":"%'+ input[i] +'%"}';
              url += ',{"name":"sponsor_id","op":"like","val":"%'+ input[i] +'%"}';
              url += ',{"name":"committees","op":"like","val":"%'+ input[i] +'%"}';
              url += ',{"name":"introduced_date","op":"like","val":"%'+ input[i] +'%"}';
              url += ',{"name":"cosponsors_by_party_R","op":"like","val":"%'+ input[i] +'%"}';
              url += ',{"name":"house_passage","op":"like","val":"%'+ input[i] +'%"}';
              url += ',{"name":"senate_passage","op":"like","val":"%'+ input[i] +'%"}';
              url += ',{"name":"cosponsors_by_party_D","op":"like","val":"%'+ input[i] +'%"}';
          }
      
          url += "]}]}&page="+ this.state.page;
        }

         if(filter === ""){
            console.log('https://api.empoweringknowledge.me/api/bills?q={"order_by":[{"field":"title","direction":"'
            + this.state.sortBy 
            + '"}]}&page='
            + this.state.page);
            fetch('https://api.empoweringknowledge.me/api/bills?q={"order_by":[{"field":"title","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, itemsToDisplay: data.objects, pages:data.total_pages,isLoading: false});      
            })
            .catch(console.log)
        }
    }
    
  handleTermChange(e) {

      this.setState({ input: e.target.value });
    
  }
  getName(value){
    if(value != null){
        return ("" + value.first_name + " " + value.last_name)
    }
  }
  handleSearch() {
    
    this.componentDidMount();
  }

  handleEnter(e) {
      
      if (e.key === 13) {
      this.handleSearch();
      }
  }

  handleSubmit(e) {
      e.preventDefault();
      this.handleSearch();
}
    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
        
    }

    handleFilterCommittee(committeeFilter){
        console.log(committeeFilter);
        this.setState({committee: committeeFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleFilterYear(yearFilter){
        console.log(yearFilter);
        this.setState({year: yearFilter, page: 1, nextpage: 2, lastpage: 1}, () =>{
        this.componentDidMount();
        });
    }

    handleSorting(sort){
        this.setState({sortBy: sort}, () => {
        this.componentDidMount();
        });
    }

    setDropdownOpen(index){
        var newdropdownOpen = this.state.dropdownOpen;
        newdropdownOpen[index] = !newdropdownOpen[index];
        this.setState({dropdownOpen: newdropdownOpen});
    }

    setDropDownTitle(attribute, selection){
        var newDropDownSelections = this.state.dropDownSelections;
        newDropDownSelections[attribute] = selection;
        this.setState({dropDownSelections: newDropDownSelections});
        if(selection === "None"){
            selection = "";
        }
        if(attribute === "committee")
            this.handleFilterCommittee(selection);
        else if(attribute === "year")
            this.handleFilterYear(selection);
    }

    renderSortAndFilter(){
      return(
        <div>
           <div className="text-left">
                <ButtonGroup>
                    <Dropdown isOpen={this.state.dropdownOpen[0]} toggle={() => this.setDropdownOpen(0)}>
                        <DropdownToggle caret>
                            Filter by Party: {this.state.dropDownSelections["party"]}
                        </DropdownToggle>
                        <DropdownMenu>
                            <DropdownItem onClick={() => this.setDropDownTitle("party", "None")}>None</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("party", "Democratic")}>Democratic</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("party", "Republican")}>Republican</DropdownItem>
                        </DropdownMenu>{' '}
                    </Dropdown>
                    <Dropdown isOpen={this.state.dropdownOpen[1]} toggle={() => this.setDropdownOpen(1)}>
                        <DropdownToggle caret>
                            Filter by Population: {this.state.dropDownSelections["population"]}
                        </DropdownToggle>
                        <DropdownMenu>
                            <DropdownItem onClick={() => this.setDropDownTitle("population", "None")}>None</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("population", "1 - 999,999")}>1 - 999,999</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("population", "1,000,000 - 9,999,999")}>1,000,000 - 9,999,999</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("population", "10,000,000 - 19,999,999")}>10,000,000 - 19,999,999</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("population", "20,000,000")}> > 20,000,000</DropdownItem>
                        </DropdownMenu>{' '}
                    </Dropdown>
                    <Dropdown isOpen={this.state.dropdownOpen[2]} toggle={() => this.setDropdownOpen(2)}>
                        <DropdownToggle caret>
                            Filter by Income: {this.state.dropDownSelections["median"]}
                        </DropdownToggle>
                        <DropdownMenu>
                            <DropdownItem onClick={() => this.setDropDownTitle("median", "None")}>None</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("median", "1 - 49,999")}>1 - 49,999</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("median", "50,000 - 59,999")}>50,000 - 59,999</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("median", "60,000 - 69,999")}>60,000 - 69,999</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("median", "70,000")}> >70,000</DropdownItem>
                        </DropdownMenu>{' '}
                    </Dropdown>
                    <Dropdown isOpen={this.state.dropdownOpen[3]} toggle={() => this.setDropdownOpen(3)}>
                        <DropdownToggle caret>
                            Filter by Energy: {this.state.dropDownSelections["resource"]}
                        </DropdownToggle>
                        <DropdownMenu>
                            <DropdownItem onClick={() => this.setDropDownTitle("resource", "None")}>None</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("resource", "NuclearPower")}>Nuclear Power</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("resource", "NaturalGas")}>Natural Gas</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("resource", "Coal")}>Coal</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("resource", "Petroleum")}>Petroleum</DropdownItem>
                            <DropdownItem onClick={() => this.setDropDownTitle("resource", "Hydroelectricity")}>Hydroelectricity</DropdownItem>
                        </DropdownMenu>{' '}
                    </Dropdown>
                    <Button onClick={() => this.handleReset()}>Reset</Button>{' '}
                </ButtonGroup>
            </div>
            <div className="text-right">
                <Button color="primary" onClick={() => this.handleSorting("asc")}>Ascending</Button>{' '}
                <Button color="secondary"onClick={() => this.handleSorting("desc")}>Descending</Button>{' '}
            </div>   
        </div>
      );
    }  


    render() {
        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)   
        
      return (
        <div>
            <div>
                {this.renderSortAndFilter()}
            </div>
        <div className="EnergyBills" style={{paddingTop: "2%",width:"100%", height:"60%"}}>
          <Form className="searchbox" onSubmit={this.handleSubmit}>
                <FormGroup row>
              
                <Col sm={{ size: 'auto', offset: 5 }}>
                    <Input
                    type="text"
                    placeholder="Search"
                    onChange={this.handleTermChange}
                    onKeyDown={this.handleEnter}
                    />
                </Col>
                </FormGroup>

                </Form>
           <div>
            <h4>Energy Bills</h4>
            <Col sm={{ size: 'auto'}} style={{paddingTop: "2%"}}>
            <Table hover responsive>
                <thead>
                <tr>
                    <th></th>
                    <th>Title</th>
                    <th>Sponsor</th>
                    <th>Committees</th>
                    <th>Introduced Date</th>
                    <th>House Passage</th>
                    <th>Senate Passage</th>
                    <th>Democrat Cosponsors</th>
                    <th>Republican Cosponsors</th>
                </tr>
                </thead>
                <tbody>
                    
                {items.map(item =>

                <tr>
                    <th scope="row"></th>
                    <td> <a href= {"/bill/" + item.bill_id}   to={"/bill/" + item.bill_id}><Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.title}/></a></td>
                    <td> <a href= {"/politician/" + item.sponsor_id}   to={"/politician/" + item.sponsor_id}><Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + this.getName(item.sponsor_rs)}/></a></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.committees}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.introduced_date}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.house_passage}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.senate_passage}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.cosponsors_by_party_D}/></td>
                    <td> <Highlighter
                                highlightClassName="HighlightClass"
                                searchWords={this.state.input.split(" ")}
                                autoEscape={true}
                                textToHighlight={"" + item.cosponsors_by_party_R}/></td>
                </tr>
                )}
                </tbody>
            </Table>
                </Col>
            </div>
        <div>
        <Pagination size="lg" style={{justifyContent: 'center', paddingTop:'3%'}} aria-label="Page navigation example">
        <PaginationItem>
          <PaginationLink first onClick={() => this.handlePageClick(1)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink previous onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"} />
        </PaginationItem>
        {/* <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"}>
            {this.state.lastpage}
          </PaginationLink>
        </PaginationItem> */}
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.page)} href={"#"}>
            {this.state.page}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"}>
            {this.state.nextpage}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink next onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink last onClick={() => this.handlePageClick(this.state.pages)} href="#" />
        </PaginationItem>
      </Pagination>
      </div>
        </div>
    </div>
      );
    }
}

export default EnergyBills;
